$create_sql[] = "CREATE TABLE `".PFX."txp_log` (
    `id` int(12) NOT NULL auto_increment,
    `time` datetime NOT NULL default '0000-00-00 00:00:00',
    `host` varchar(255) NOT NULL default '',
    `page` varchar(255) NOT NULL default '',
    `refer` mediumtext NOT NULL,
    `status` int(11) NOT NULL default '200',
    `method` varchar(16) NOT NULL default 'GET',
    `ip` varchar(16) NOT NULL default '',
    PRIMARY KEY  (`id`),
    KEY `time` (`time`)
) $tabletype AUTO_INCREMENT=77 ";

$create_sql[] = "CREATE TABLE `".PFX."txp_page` (
    `name` varchar(128) NOT NULL default '',
    `user_html` text NOT NULL,
    PRIMARY KEY (`name`)
) $tabletype PACK_KEYS=1";

// sql:txp_page
$create_sql[] = "INSERT INTO `".PFX."txp_page`(`name`,`user_html`) VALUES('archive', '<!DOCTYPE html>\n<html lang=\"<txp:lang />\" dir=\"<txp:text item=\"lang_dir\" />\">\n\n<head>\n    <meta charset=\"utf-8\">\n    <title>\n        <txp:page_title />\n    </title>\n    <meta name=\"generator\" content=\"Textpattern CMS\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n    <meta name=\"robots\" content=\"index, follow, noodp, noydir\">\n\n    <txp:if_individual_article>\n        <!-- add meta author for individual articles -->\n        <txp:meta_author title=\"1\" />\n    </txp:if_individual_article>\n\n    <!-- content feeds -->\n    <txp:feed_link flavor=\"atom\" format=\"link\" label=\"Atom\" />\n    <txp:feed_link flavor=\"rss\" format=\"link\" label=\"RSS\" />\n    <txp:rsd />\n\n    <!-- specify canonical, more info: http://googlewebmastercentral.blogspot.com/2009/02/specify-your-canonical.html -->\n    <txp:if_individual_article>\n        <link rel=\"canonical\" href=\"<txp:permlink />\">\n    <txp:else />\n        <link rel=\"canonical\" href=\"<txp:section url=\"1\" />\">\n    </txp:if_individual_article>\n\n    <!-- CSS -->\n    <!-- Google font API (remove this if you intend to use the theme in a project without internet access) -->\n    <link rel=\"stylesheet\" href=\"http://fonts.googleapis.com/css?family=PT+Serif:400,400italic,700,700italic\">\n\n    <txp:css format=\"link\" media=\"\" />\n    <!-- ...or you can use (faster) external CSS files eg. <link rel=\"stylesheet\" href=\"<txp:site_url />css/default.css\"> -->\n\n    <!-- HTML5/Media Queries support for IE 8 (you can remove this section and the corresponding\n         \'js\' directory file if you don\'t intend to support IE 8) -->\n    <!--[if lt IE 9]>\n        <script src=\"<txp:site_url />js/html5shiv.js\"></script>\n        <txp:css format=\"link\" media=\"\" name=\"ie8\" />\n    <![endif]-->\n\n</head>\n\n<body class=\"<txp:section />-page\">\n\n    <!-- header -->\n    <header role=\"banner\">\n        <h1>\n            <txp:link_to_home>\n                <txp:site_name />\n            </txp:link_to_home>\n        </h1>\n        <h3>\n            <txp:site_slogan />\n        </h3>\n    </header>\n\n    <!-- navigation -->\n    <nav class=\"site-navigation\" role=\"navigation\" aria-label=\"<txp:text item=\"navigation\" />\">\n        <txp:section_list default_title=\'<txp:text item=\"home\" />\' include_default=\"1\" wraptag=\"ul\" break=\"\">\n            <li<txp:if_section name=\'<txp:section />\'> class=\"active\"</txp:if_section>>\n                <txp:section title=\"1\" link=\"1\" />\n            </li>\n        </txp:section_list>\n    </nav>\n\n    <div class=\"wrapper\">\n        <div class=\"container\">\n\n            <!-- left (main) column -->\n            <main role=\"main\" aria-label=\"<txp:text item=\"main_content\" />\">\n\n                <txp:if_article_list>\n\n                    <section role=\"region\" itemscope itemtype=\"http://schema.org/CollectionPage\">\n                        <h1 itemprop=\"name\">\n                            <txp:section title=\"1\" />\n                        </h1>\n                        <div itemprop=\"mainContentOfPage\">\n                            <txp:if_article_list>\n\n                                <txp:article class=\"article-list\" form=\"article_listing\" limit=\"5\" wraptag=\"ul\" />\n                                <!-- ...or if you want to list all articles from all sections instead, then replace txp:article with txp:article_custom -->\n\n                            <txp:else />\n                                <txp:article />\n                            </txp:if_article_list>\n                        </div>\n                    </section>\n\n                <txp:else />\n\n                    <txp:article />\n\n                </txp:if_article_list>\n\n                <!-- add pagination links to foot of article/article listings if there are more articles available,\n                     this method is more flexible than using simple txp:link_to_prev/txp:link_to_next or txp:older/txp:newer tags -->\n                <txp:if_individual_article>\n\n                    <txp:variable name=\"more\" value=\'<txp:link_to_prev /><txp:link_to_next />\' />\n                    <txp:variable name=\"prev\" value=\'<txp:link_to_prev />\' />\n                    <txp:variable name=\"next\" value=\'<txp:link_to_next />\' />\n\n                    <txp:if_variable name=\"more\" value=\"\">\n                    <txp:else />\n                        <p class=\"paginator\">\n                            <txp:if_variable name=\"prev\" value=\"\">\n                            <txp:else />\n                                <a rel=\"prev\" href=\"<txp:link_to_prev />\" title=\"<txp:prev_title />\">\n                                     <txp:text item=\"older\" />\n                                </a>\n                            </txp:if_variable>\n\n                            <txp:if_variable name=\"next\" value=\"\">\n                            <txp:else />\n                                <a rel=\"next\" href=\"<txp:link_to_next />\" title=\"<txp:next_title />\">\n                                    <txp:text item=\"newer\" /> \n                                </a>\n                            </txp:if_variable>\n                        </p>\n                    </txp:if_variable>\n\n                <txp:else />\n\n                    <txp:variable name=\"more\" value=\'<txp:older /><txp:newer />\' />\n                    <txp:variable name=\"prev\" value=\'<txp:older />\' />\n                    <txp:variable name=\"next\" value=\'<txp:newer />\' />\n\n                    <txp:if_variable name=\"more\" value=\"\">\n                    <txp:else />\n                        <p class=\"paginator\">\n                            <txp:if_variable name=\"prev\" value=\"\">\n                            <txp:else />\n                                <a rel=\"prev\" href=\"<txp:older />\" title=\"<txp:text item=\"older\" />\">\n                                     <txp:text item=\"older\" />\n                                </a>\n                            </txp:if_variable>\n\n                            <txp:if_variable name=\"next\" value=\"\">\n                            <txp:else />\n                                <a rel=\"next\" href=\"<txp:newer />\" title=\"<txp:text item=\"newer\" />\">\n                                    <txp:text item=\"newer\" /> \n                                </a>\n                            </txp:if_variable>\n                        </p>\n                    </txp:if_variable>\n\n                </txp:if_individual_article>\n\n            </main>\n\n            <!-- right (complementary) column -->\n            <div role=\"complementary\">\n\n                <!-- links by default to form: \'search_input.misc.txp\' unless you specify a different form -->\n                <txp:search_input />\n\n                <!-- feed links, default flavor is RSS, so we don\'t need to specify a flavor on the first feed_link -->\n                <p>\n                    <txp:feed_link class=\"feed-rss\" label=\"RSS\" />\n                    /\n                    <txp:feed_link class=\"feed-atom\" flavor=\"atom\" label=\"Atom\" />\n                </p>\n\n                <!-- if links exist, renders a links list -->\n                <txp:variable name=\"external_links\" value=\'<txp:linklist limit=\"1\" />\' />\n\n                <txp:if_variable name=\"external_links\" value=\"\">\n                <txp:else />\n                    <h4>\n                        <txp:text item=\"external_links\" />\n                    </h4>\n\n                    <!-- links by default to form: \'plainlinks.link.txp\' unless you specify a different form -->\n                    <txp:linklist wraptag=\"ul\" break=\"li\" limit=\"10\" />\n                </txp:if_variable>\n\n            </div> <!-- /complementary -->\n\n        </div> <!-- /.container -->\n    </div> <!-- /.wrapper -->\n\n    <!-- footer -->\n    <footer role=\"contentinfo\">\n        <p>\n            <small>\n                <txp:text item=\"published_with\" />\n                <a rel=\"external\" href=\"http://textpattern.com\" title=\"<txp:text item=\"go_txp_com\" />\">Textpattern CMS</a>\n            </small>\n        </p>\n    </footer>\n\n    <!-- add your own JavaScript here -->\n\n</body>\n</html>\n')";
$create_sql[] = "INSERT INTO `".PFX."txp_page`(`name`,`user_html`) VALUES('default', '<!DOCTYPE html>\n<html lang=\"<txp:lang />\" dir=\"<txp:text item=\"lang_dir\" />\">\n\n<head>\n    <meta charset=\"utf-8\">\n    <title>\n        <txp:page_title />\n    </title>\n    <meta name=\"generator\" content=\"Textpattern CMS\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n\n    <txp:if_search>\n        <meta name=\"robots\" content=\"none\">\n    <txp:else />\n        <txp:if_category>\n            <meta name=\"robots\" content=\"noindex, follow, noodp, noydir\">\n        <txp:else />\n            <txp:if_author>\n                <meta name=\"robots\" content=\"noindex, follow, noodp, noydir\">\n            <txp:else />\n                <meta name=\"robots\" content=\"index, follow, noodp, noydir\">\n            </txp:if_author>\n        </txp:if_category>\n    </txp:if_search>\n\n    <!-- content feeds -->\n    <txp:feed_link flavor=\"atom\" format=\"link\" label=\"Atom\" />\n    <txp:feed_link flavor=\"rss\" format=\"link\" label=\"RSS\" />\n    <txp:rsd />\n\n    <!-- specify canonical, more info: http://googlewebmastercentral.blogspot.com/2009/02/specify-your-canonical.html -->\n    <txp:if_section name=\"\">\n        <link rel=\"canonical\" href=\"<txp:site_url />\">\n    <txp:else />\n        <txp:if_individual_article>\n            <link rel=\"canonical\" href=\"<txp:permlink />\">\n        <txp:else />\n            <link rel=\"canonical\" href=\"<txp:section url=\"1\" />\">\n        </txp:if_individual_article>\n    </txp:if_section>\n\n    <!-- CSS -->\n    <!-- Google font API (remove this if you intend to use the theme in a project without internet access) -->\n    <link rel=\"stylesheet\" href=\"http://fonts.googleapis.com/css?family=PT+Serif:400,400italic,700,700italic\">\n\n    <txp:css format=\"link\" media=\"\" />\n    <!-- ...or you can use (faster) external CSS files eg. <link rel=\"stylesheet\" href=\"<txp:site_url />css/default.css\"> -->\n\n    <!-- HTML5/Media Queries support for IE 8 (you can remove this section and the corresponding\n         \'js\' directory file if you don\'t intend to support IE 8) -->\n    <!--[if lt IE 9]>\n        <script src=\"<txp:site_url />js/html5shiv.js\"></script>\n        <txp:css format=\"link\" media=\"\" name=\"ie8\" />\n    <![endif]-->\n\n</head>\n\n<body class=\"<txp:if_section name=\"\"><txp:if_search>search<txp:else />front</txp:if_search><txp:else /><txp:section /></txp:if_section>-page\">\n\n    <!-- header -->\n    <header role=\"banner\">\n        <h1>\n            <txp:link_to_home>\n                <txp:site_name />\n            </txp:link_to_home>\n        </h1>\n        <h3>\n            <txp:site_slogan />\n        </h3>\n    </header>\n\n    <!-- navigation -->\n    <nav class=\"site-navigation\" role=\"navigation\" aria-label=\"<txp:text item=\"navigation\" />\">\n        <txp:section_list default_title=\'<txp:text item=\"home\" />\' include_default=\"1\" wraptag=\"ul\" break=\"\">\n            <li<txp:if_section name=\'<txp:section />\'><txp:if_search><txp:else /><txp:if_category><txp:else /><txp:if_author><txp:else /> class=\"active\"</txp:if_author></txp:if_category></txp:if_search></txp:if_section>>\n                <txp:section title=\"1\" link=\"1\" />\n            </li>\n        </txp:section_list>\n    </nav>\n\n    <div class=\"wrapper\">\n        <div class=\"container\">\n\n            <!-- left (main) column -->\n            <main role=\"main\" aria-label=\"<txp:text item=\"main_content\" />\">\n\n                <!-- is this the search result page? also omits the pagination links below (uses pagination format within search_results.article.txp instead) -->\n                <txp:if_search>\n\n                    <section role=\"region\" itemscope itemtype=\"http://schema.org/SearchResultsPage\">\n                        <h1 itemprop=\"name\">\n                            <txp:text item=\"search_results\" />\n                        </h1>\n                        <div itemprop=\"mainContentOfPage\">\n                            <txp:output_form form=\"search_results\" />\n                        </div>\n                    </section>\n\n                <txp:else />\n\n                    <!-- else is this an article category list? -->\n                    <txp:if_category>\n\n                        <section role=\"region\" itemscope itemtype=\"http://schema.org/CollectionPage\">\n                            <h1 itemprop=\"name\">\n                                <txp:text item=\"category\" />\n                                <txp:category title=\"1\" />\n                            </h1>\n                            <div itemprop=\"mainContentOfPage\">\n                                <txp:article form=\"article_listing\" limit=\"5\" wraptag=\"ul\" class=\"article-list\" />\n                            </div>\n                        </section>\n\n                    <txp:else />\n\n                        <!-- else is this an article author list? -->\n                        <txp:if_author>\n\n                            <section role=\"region\" itemscope itemtype=\"http://schema.org/CollectionPage\">\n                                <h1 itemprop=\"name\">\n                                    <txp:text item=\"author\" />\n                                    <txp:author />\n                                </h1>\n                                <div itemprop=\"mainContentOfPage\">\n                                    <txp:article form=\"article_listing\" limit=\"5\" wraptag=\"ul\" class=\"article-list\" />\n                                </div>\n                            </section>\n\n                        <!-- else display articles normally -->\n                        <txp:else />\n\n                            <!-- links by default to form: \'default.article.txp\' unless you specify a different form -->\n                            <txp:article limit=\"5\" />\n\n                        </txp:if_author>\n                    </txp:if_category>\n\n                    <!-- add pagination links to foot of article/article listings/category listings if there are more articles available,\n                         this method is more flexible than using simple txp:link_to_prev/txp:link_to_next or txp:older/txp:newer tags -->\n                    <txp:variable name=\"more\" value=\'<txp:older /><txp:newer />\' />\n                    <txp:variable name=\"prev\" value=\'<txp:older />\' />\n                    <txp:variable name=\"next\" value=\'<txp:newer />\' />\n\n                    <txp:if_variable name=\"more\" value=\"\">\n                    <txp:else />\n                        <p class=\"paginator\">\n                            <txp:if_variable name=\"prev\" value=\"\">\n                            <txp:else />\n                                <a rel=\"prev\" href=\"<txp:older />\" title=\"<txp:text item=\"older\" />\">\n                                     <txp:text item=\"older\" />\n                                </a>\n                            </txp:if_variable>\n\n                            <txp:if_variable name=\"next\" value=\"\">\n                            <txp:else />\n                                <a rel=\"next\" href=\"<txp:newer />\" title=\"<txp:text item=\"newer\" />\">\n                                    <txp:text item=\"newer\" /> \n                                </a>\n                            </txp:if_variable>\n                        </p>\n                    </txp:if_variable>\n\n                </txp:if_search>\n\n            </main>\n\n            <!-- right (complementary) column -->\n            <div role=\"complementary\">\n\n                <!-- links by default to form: \'search_input.misc.txp\' unless you specify a different form -->\n                <txp:search_input />\n\n                <!-- feed links, default flavor is RSS, so we don\'t need to specify a flavor on the first feed_link -->\n                <p>\n                    <txp:feed_link class=\"feed-rss\" label=\"RSS\" />\n                    /\n                    <txp:feed_link class=\"feed-atom\" flavor=\"atom\" label=\"Atom\" />\n                </p>\n\n                <!-- if links exist, renders a links list -->\n                <txp:variable name=\"external_links\" value=\'<txp:linklist limit=\"1\" />\' />\n\n                <txp:if_variable name=\"external_links\" value=\"\">\n                <txp:else />\n                    <h4>\n                        <txp:text item=\"external_links\" />\n                    </h4>\n\n                    <!-- links by default to form: \'plainlinks.link.txp\' unless you specify a different form -->\n                    <txp:linklist wraptag=\"ul\" break=\"li\" limit=\"10\" />\n                </txp:if_variable>\n\n            </div> <!-- /complementary -->\n\n        </div> <!-- /.container -->\n    </div> <!-- /.wrapper -->\n\n    <!-- footer -->\n    <footer role=\"contentinfo\">\n        <p>\n            <small>\n                <txp:text item=\"published_with\" />\n                <a rel=\"external\" href=\"http://textpattern.com\" title=\"<txp:text item=\"go_txp_com\" />\">Textpattern CMS</a>\n            </small>\n        </p>\n    </footer>\n\n    <!-- add your own JavaScript here -->\n\n</body>\n</html>\n')";
$create_sql[] = "INSERT INTO `".PFX."txp_page`(`name`,`user_html`) VALUES('error_default', '<!DOCTYPE html>\n<html lang=\"<txp:lang />\" dir=\"<txp:text item=\"lang_dir\" />\">\n\n<head>\n    <meta charset=\"utf-8\">\n    <title>\n        <txp:error_status />\n    </title>\n    <meta name=\"generator\" content=\"Textpattern CMS\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n    <meta name=\"robots\" content=\"none\">\n\n    <!-- content feeds -->\n    <txp:feed_link flavor=\"atom\" format=\"link\" label=\"Atom\" />\n    <txp:feed_link flavor=\"rss\" format=\"link\" label=\"RSS\" />\n    <txp:rsd />\n\n    <!-- CSS -->\n    <!-- Google font API (remove this if you intend to use the theme in a project without internet access) -->\n    <link rel=\"stylesheet\" href=\"http://fonts.googleapis.com/css?family=PT+Serif:400,400italic,700,700italic\">\n\n    <txp:css format=\"link\" media=\"\" />\n    <!-- ...or you can use (faster) external CSS files eg. <link rel=\"stylesheet\" href=\"<txp:site_url />css/default.css\"> -->\n\n    <!-- HTML5/Media Queries support for IE 8 (you can remove this section and the corresponding\n         \'js\' directory file if you don\'t intend to support IE 8) -->\n    <!--[if lt IE 9]>\n        <script src=\"<txp:site_url />js/html5shiv.js\"></script>\n        <txp:css format=\"link\" media=\"\" name=\"ie8\" />\n    <![endif]-->\n\n</head>\n\n<body class=\"error-page\">\n\n    <!-- header -->\n    <header role=\"banner\">\n        <h1>\n            <txp:link_to_home>\n                <txp:site_name />\n            </txp:link_to_home>\n        </h1>\n        <h3>\n            <txp:site_slogan />\n        </h3>\n    </header>\n\n    <!-- navigation -->\n    <nav class=\"site-navigation\" role=\"navigation\" aria-label=\"<txp:text item=\"navigation\" />\">\n        <txp:section_list default_title=\'<txp:text item=\"home\" />\' include_default=\"1\" wraptag=\"ul\" break=\"li\">\n            <txp:section title=\"1\" link=\"1\" />\n        </txp:section_list>\n    </nav>\n\n    <div class=\"wrapper\">\n        <div class=\"container\">\n\n            <!-- left (main) column -->\n            <main role=\"main\" aria-label=\"<txp:text item=\"main_content\" />\">\n                <h1 class=\"error-status\">\n                    <txp:error_status />\n                </h1>\n                <p class=\"error-msg\">\n                    <txp:error_message />\n                </p>\n            </main>\n\n            <!-- right (complementary) column -->\n            <div role=\"complementary\">\n\n                <!-- links by default to form: \'search_input.misc.txp\' unless you specify a different form -->\n                <txp:search_input />\n\n                <!-- feed links, default flavor is RSS, so we don\'t need to specify a flavor on the first feed_link -->\n                <p>\n                    <txp:feed_link class=\"feed-rss\" label=\"RSS\" />\n                    /\n                    <txp:feed_link class=\"feed-atom\" flavor=\"atom\" label=\"Atom\" />\n                </p>\n\n                <!-- if links exist, renders a links list -->\n                <txp:variable name=\"external_links\" value=\'<txp:linklist limit=\"1\" />\' />\n\n                <txp:if_variable name=\"external_links\" value=\"\">\n                <txp:else />\n                    <h4>\n                        <txp:text item=\"external_links\" />\n                    </h4>\n\n                    <!-- links by default to form: \'plainlinks.link.txp\' unless you specify a different form -->\n                    <txp:linklist wraptag=\"ul\" break=\"li\" limit=\"10\" />\n                </txp:if_variable>\n\n            </div> <!-- /complementary -->\n\n        </div> <!-- /.container -->\n    </div> <!-- /.wrapper -->\n\n    <!-- footer -->\n    <footer role=\"contentinfo\">\n        <p>\n            <small>\n                <txp:text item=\"published_with\" />\n                <a rel=\"external\" href=\"http://textpattern.com\" title=\"<txp:text item=\"go_txp_com\" />\">Textpattern CMS</a>\n            </small>\n        </p>\n    </footer>\n\n    <!-- add your own JavaScript here -->\n\n</body>\n</html>\n')";
